import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useVideo } from '../context/VideoContext';
import { 
  Search, 
  Play, 
  Clock, 
  Sparkles,
  Send,
  Paperclip,
  ChevronDown,
  Star,
  Heart,
  X,
  Video,
  MessageSquare,
  Plus,
  Loader2,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import VideoPlayer from './VideoPlayer';
import toast from 'react-hot-toast';

const AIVideoStudio = () => {
  const { videos, generateVideo, loading, fetchVideos, updateVideo } = useVideo();
  const [searchTerm, setSearchTerm] = useState('');
  const [prompt, setPrompt] = useState('');
  const [selectedModel, setSelectedModel] = useState('Runway Gen-4 Turbo');
  const [showLovableBanner, setShowLovableBanner] = useState(true);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    fetchVideos();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [chatMessages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Function to detect if the prompt is asking to enhance/modify an existing video
  const isEnhancementPrompt = (prompt) => {
    const enhancementKeywords = [
      'enhance', 'improve', 'modify', 'change', 'add', 'remove', 'make it', 
      'make the', 'update', 'edit', 'adjust', 'fix', 'better', 'more',
      'less', 'brighter', 'darker', 'faster', 'slower', 'longer', 'shorter'
    ];

    const lowerPrompt = prompt.toLowerCase();
    return enhancementKeywords.some(keyword => lowerPrompt.includes(keyword));
  };

  // Function to get the most recent video from chat messages
  const getLatestVideoFromChat = () => {
    // Look for the most recent video in chat messages
    for (let i = chatMessages.length - 1; i >= 0; i--) {
      const message = chatMessages[i];
      if (message.videoUrl && message.videoId) {
        return {
          videoId: message.videoId,
          videoUrl: message.videoUrl,
          prompt: message.originalPrompt || 'previous video'
        };
      }
    }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim() || loading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: prompt,
      timestamp: new Date()
    };

    setChatMessages(prev => [...prev, userMessage]);
    setPrompt('');
    setIsGenerating(true);

    try {
      const result = await generateVideo(prompt, 5, '16:9', selectedModel);

      const aiMessage = {
        id: Date.now() + 1,
        type: 'ai',
        content: 'I\'m generating your video...',
        timestamp: new Date(),
        videoId: result.videoId,
        status: 'generating'
      };

      setChatMessages(prev => [...prev, aiMessage]);

      // Start polling for video completion
      pollVideoStatus(result.videoId);

    } catch (error) {
      console.error('Error generating video:', error);
      toast.error('Failed to generate video');

      const errorMessage = {
        id: Date.now() + 1,
        type: 'ai',
        content: 'Sorry, I couldn\'t generate your video. Please try again.',
        timestamp: new Date(),
        isError: true
      };

      setChatMessages(prev => [...prev, errorMessage]);
      setIsGenerating(false);
    }
  };

  const pollVideoStatus = async (videoId) => {
    const maxAttempts = 60; // 5 minutes max
    let attempts = 0;

    const poll = async () => {
      try {
        const response = await fetch(`/api/videos/${videoId}/status`);
        const data = await response.json();

        if (data.status === 'completed' && data.videoUrl) {
          // Update the AI message with the completed video
          setChatMessages(prev => prev.map(msg => 
            msg.videoId === videoId 
              ? { ...msg, content: 'Here\'s your generated video!', videoUrl: data.videoUrl, status: 'completed' }
              : msg
          ));
          setIsGenerating(false);
          toast.success('Video generated successfully!');
          return;
        } else if (data.status === 'failed') {
          setChatMessages(prev => prev.map(msg => 
            msg.videoId === videoId 
              ? { ...msg, content: 'Sorry, video generation failed. Please try again.', status: 'failed', isError: true }
              : msg
          ));
          setIsGenerating(false);
          toast.error('Video generation failed');
          return;
        }

        attempts++;
        if (attempts < maxAttempts) {
          setTimeout(poll, 5000); // Poll every 5 seconds
        } else {
          setChatMessages(prev => prev.map(msg => 
            msg.videoId === videoId 
              ? { ...msg, content: 'Video generation is taking longer than expected. Please check back later.', status: 'timeout' }
              : msg
          ));
          setIsGenerating(false);
        }
      } catch (error) {
        console.error('Error polling video status:', error);
        setIsGenerating(false);
      }
    };

    poll();
  };

  const handleVideoClick = (video) => {
    setSelectedVideo(video);

    // Create chat context for this video
    const initialMessages = [
      {
        id: 1,
        type: 'ai',
        content: `Here's your video: "${video.title || 'Untitled Video'}". You can ask me to enhance it, modify it, or create variations. What would you like to do?`,
        timestamp: new Date(),
        videoUrl: video.videoUrl,
        videoId: video.videoId
      }
    ];

    setChatMessages(initialMessages);
  };

  const handleEnhanceVideo = async (enhancementPrompt) => {
    if (!selectedVideo || !enhancementPrompt.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: enhancementPrompt,
      timestamp: new Date()
    };

    setChatMessages(prev => [...prev, userMessage]);
    setIsGenerating(true);

    try {
      // For now, we'll create a new video with the enhancement prompt
      // In a real implementation, you might want to use video-to-video generation
      const result = await generateVideo(`${enhancementPrompt} based on this video: ${selectedVideo.prompt || 'previous video'}`, 5, '16:9', selectedModel);

      const aiMessage = {
        id: Date.now() + 1,
        type: 'ai',
        content: 'I\'m enhancing your video...',
        timestamp: new Date(),
        videoId: result.videoId,
        status: 'generating'
      };

      setChatMessages(prev => [...prev, aiMessage]);
      pollVideoStatus(result.videoId);

    } catch (error) {
      console.error('Error enhancing video:', error);
      toast.error('Failed to enhance video');
      setIsGenerating(false);
    }
  };

  const startNewChat = () => {
    setSelectedVideo(null);
    setChatMessages([
      {
        id: 1,
        type: 'ai',
        content: 'Welcome to AI Video Studio! I can help you create amazing videos. Just describe what you want to see and I\'ll generate it for you. You can also edit and enhance your videos with simple prompts.',
        timestamp: new Date()
      }
    ]);
  };

  const filteredVideos = videos.filter(video =>
    (video.title && video.title.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (video.prompt && video.prompt.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="h-screen bg-gradient-to-br from-purple-50/20 via-white to-white flex flex-col">
      {/* Header */}
      <motion.header
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white/80 backdrop-blur-md border-b border-gray-200 px-6 py-4 flex items-center justify-between"
      >
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-gradient-to-br from-primary-500 to-primary-600 rounded-xl flex items-center justify-center">
            <Star className="h-6 w-6 text-white" />
          </div>
          <h1 className="text-xl font-bold text-gray-900">AI Video Studio</h1>
        </div>

        {/* Model Selector and Right side text */}
        <div className="flex items-center space-x-4">
          {/* Model Selection */}
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Model:</span>
            <div className="relative">
              <select
                value={selectedModel}
                onChange={(e) => setSelectedModel(e.target.value)}
                className="appearance-none bg-white border border-gray-200 rounded-lg px-3 py-1.5 pr-8 text-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                <option value="Runway Gen-4 Turbo">Runway Gen-4 Turbo</option>
                <option value="Runway Gen-3">Runway Gen-3</option>
                <option value="Veo3">Veo3</option>
              </select>
              <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 h-3 w-3 text-gray-400 pointer-events-none" />
            </div>
          </div>

          <div className="flex items-center space-x-2 text-gray-600">
            <span className="text-sm">Next-gen AI video creation</span>
            <Sparkles className="h-4 w-4 text-primary-500" />
          </div>
        </div>
      </motion.header>

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden">
        {/* Left Sidebar - Video Library */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
          className="w-80 bg-white/60 backdrop-blur-sm border-r border-gray-200 flex flex-col"
        >
          {/* Sidebar Header */}
          <div className="p-4 border-b border-gray-200">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-gray-900">Video Library</h2>
              <button
                onClick={startNewChat}
                className="p-2 bg-primary-500 text-white rounded-lg hover:bg-primary-600 transition-colors"
                title="Start New Chat"
              >
                <Plus className="h-4 w-4" />
              </button>
            </div>

            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search videos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent text-sm"
              />
            </div>
          </div>

          {/* Video List */}
          <div className="flex-1 overflow-y-auto p-4 space-y-3">
            {filteredVideos.length === 0 ? (
              <div className="text-center text-gray-500 py-8">
                <Video className="h-12 w-12 mx-auto mb-3 text-gray-300" />
                <p>No videos found</p>
              </div>
            ) : (
              filteredVideos.map((video) => (
                <motion.div
                  key={video.videoId}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => handleVideoClick(video)}
                  className={`p-3 rounded-lg border cursor-pointer transition-all ${
                    selectedVideo?.videoId === video.videoId
                      ? 'bg-primary-50 border-primary-200 shadow-sm'
                      : 'bg-white border-gray-200 hover:border-gray-300 hover:shadow-sm'
                  }`}
                >
                  <div className="flex items-start space-x-3">
                    <div className="w-12 h-8 bg-gray-100 rounded flex items-center justify-center flex-shrink-0">
                      {video.status === 'completed' ? (
                        <Play className="h-4 w-4 text-gray-600" />
                      ) : video.status === 'generating' ? (
                        <Loader2 className="h-4 w-4 text-primary-500 animate-spin" />
                      ) : (
                        <AlertCircle className="h-4 w-4 text-red-500" />
                      )}
                    </div>
                    <div className="flex-1 min-w-0">
                      <h3 className="text-sm font-medium text-gray-900 truncate">
                        {video.title || 'Untitled Video'}
                      </h3>
                      <p className="text-xs text-gray-500 mt-1 line-clamp-2">
                        {video.prompt || 'No prompt available'}
                      </p>
                      <div className="flex items-center space-x-2 mt-2">
                        <span className="text-xs text-gray-400">
                          {video.createdAt ? new Date(video.createdAt).toLocaleDateString() : 'Unknown date'}
                        </span>
                        {video.duration && (
                          <span className="text-xs text-gray-400 flex items-center">
                            <Clock className="h-3 w-3 mr-1" />
                            {video.duration}s
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                </motion.div>
              ))
            )}
          </div>
        </motion.div>

        {/* Main Chat Area */}
        <div className="flex-1 flex flex-col">
          {/* Chat Messages */}
          <div className="flex-1 overflow-y-auto p-6 space-y-4">
            {chatMessages.map((message) => (
              <motion.div
                key={message.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div className={`max-w-2xl ${message.type === 'user' ? 'order-2' : 'order-1'}`}>
                  <div className={`flex items-start space-x-3 ${message.type === 'user' ? 'flex-row-reverse space-x-reverse' : ''}`}>
                    {/* Avatar */}
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
                      message.type === 'user' 
                        ? 'bg-primary-500' 
                        : 'bg-gradient-to-br from-primary-500 to-primary-600'
                    }`}>
                      {message.type === 'user' ? (
                        <span className="text-white text-sm font-medium">U</span>
                      ) : (
                        <Star className="h-4 w-4 text-white" />
                      )}
                    </div>

                    {/* Message Content */}
                    <div className={`rounded-2xl px-4 py-3 ${
                      message.type === 'user'
                        ? 'bg-primary-500 text-white'
                        : message.isError
                        ? 'bg-red-50 text-red-800 border border-red-200'
                        : 'bg-white border border-gray-200 shadow-sm'
                    }`}>
                      <p className="text-sm">{message.content}</p>

                      {/* Video Content */}
                      {message.videoUrl && (
                        <div className="mt-3">
                          <VideoPlayer videoUrl={message.videoUrl} />
                        </div>
                      )}

                      {/* Loading State */}
                      {message.status === 'generating' && (
                        <div className="mt-3 flex items-center space-x-2 text-primary-600">
                          <Loader2 className="h-4 w-4 animate-spin" />
                          <span className="text-sm">Generating video...</span>
                        </div>
                      )}

                      {/* Timestamp */}
                      <div className={`text-xs mt-2 ${
                        message.type === 'user' ? 'text-primary-100' : 'text-gray-400'
                      }`}>
                        {message.timestamp.toLocaleTimeString()}
                      </div>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.5 }}
            className="p-6 border-t border-gray-200 bg-white/80 backdrop-blur-sm"
          >
            <div className="bg-white rounded-2xl p-4 shadow-lg border border-gray-200">
              <div className="flex items-center space-x-4">
                {/* Input Field */}
                <div className="flex-1 relative">
                  <input
                    type="text"
                    placeholder={selectedVideo ? "Enhance this video..." : "Describe the video you want to create..."}
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSubmit(e)}
                    className="w-full px-4 py-3 pr-20 bg-secondary-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent text-sm"
                    disabled={isGenerating}
                  />

                  {/* Action Buttons */}
                  <div className="absolute right-2 top-1/2 transform -translate-y-1/2 flex items-center space-x-1">
                    <button className="p-1.5 text-gray-400 hover:text-gray-600 transition-colors">
                      <Paperclip className="h-4 w-4" />
                    </button>
                    <button
                      onClick={handleSubmit}
                      disabled={isGenerating || !prompt.trim()}
                      className="p-1.5 bg-primary-600 text-white rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                    >
                      {isGenerating ? (
                        <Loader2 className="h-4 w-4 animate-spin" />
                      ) : (
                        <Send className="h-4 w-4" />
                      )}
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>

    </div>
  );
};

export default AIVideoStudio;
